	slidersInit();

	$(window).resize(function(event) {
		slidersInit();
	});

	function slidersInit () {
		var media = window.matchMedia('(max-width: 980px)').matches;

		if (media) {
			if ( !$('.another-slider').hasClass('slick-initialized') ) {
				$('.another-slider').slick({
					infinite: false,
					slidesToShow: 2,
					slidesToScroll: 1,
					dots: true,
					arrows: true,
					responsive: [
					{
						breakpoint: 641,
						settings: {
							slidesToShow: 1,
							slidesToScroll: 1,
							adaptiveHeight: true
						}
					}
					]
				});
			};
			// -------------------
		} else{
			if ( $('.another-slider').hasClass('slick-initialized') ) {
				$('.another-slider').slick('unslick');
			};
			// -------------------
		};
	}

	$('form').each(function(index, el) {
		$(el).validate({
			rules:{
				'phone':{ required:true }
				// 'mail':{ required:true, email: true }
			},
			submitHandler: function(form){
				$(form).ajaxSubmit({
					type: 'POST',
					url: 'mail.php',
					success: function() {
						$(form).trigger( 'reset' );
						document.location.href = "thanks.html";
						if ( $(form).hasClass('download') ) {
							var link = document.createElement('a');
							link.setAttribute('href','pdf/women.pdf');
							link.setAttribute('download','download');
							link.click();
						};
					}
				});
			}
		});
	});
